        borderWidth: 1,
    },
    viewStyle3PierChart: {
        backgroundColor: AppColors.pierChartColor3,
        width: 20,
        height: 20, 
        borderWidth: 1,
    },
    viewStyle4PierChart: {
        backgroundColor: AppColors.pierChartColor4, 
        width: 20, 
        height: 20, 
        borderWidth: 1,
    },
    viewStyle5PierChartccv: {
        backgroundColor: AppColors.pierChartColor5, 
        width: 20, 
